INSERT INTO `ad_admin_log` VALUES (40, 1, 'admin', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10.14; rv:68.0) Gecko/20100101 Firefox/68.0', '111.194.49.211', 'http://blog.kevinfei.com/admin/article/edit/id/54.html', 'POST', 'json', '{\"image\":\"\",\"cid\":\"2\",\"title\":\"\\u8f6c\\uff1a\\u8ba9PHP7\\u8fbe\\u5230\\u6700\\u9ad8\\u6027\\u80fd\\u7684\\u51e0\\u4e2aTips\",\"author\":\"\\u6210\\u957f\\u7684\\u5c0f\\u767d\\u83dc\",\"summary\":\"PHP7\\u5df2\\u7ecf\\u53d1\\u5e03\\u4e86,  \\u4f5c\\u4e3aPHP10\\u5e74\\u6765\\u6700\\u5927\\u7684\\u7248\\u672c\\u5347\\u7ea7, \\u6700\\u5927\\u7684\\u6027\\u80fd\\u5347\\u7ea7, PHP7\\u5728\\u591a\\u653e\\u7684\\u6d4b\\u8bd5\\u4e2d\\u90fd\\u8868\\u73b0\\u51fa\\u5f88\\u660e\\u663e\\u7684\\u6027\\u80fd\\u63d0\\u5347, \\u7136\\u800c, \\u4e3a\\u4e86\\u8ba9\\u5b83\\u80fd\\u53d1\\u6325\\u51fa\\u6700\\u5927\\u7684\\u6027\\u80fd, \\u6211\\u8fd8\\u662f\\u6709\\u51e0\\u4ef6\\u4e8b\\u60f3\\u63d0\\u9192\\u4e0b.\",\"photo\":\"\\/public\\/upload\\/image\\/20190813\\/efee0c8385c3f0733de87af8c6e0ceec.jpg\",\"content\":\"<p>PHP7\\u5df2\\u7ecf\\u53d1\\u5e03\\u4e86, &nbsp;\\u4f5c\\u4e3aPHP10\\u5e74\\u6765\\u6700\\u5927\\u7684\\u7248\\u672c\\u5347\\u7ea7, \\u6700\\u5927\\u7684\\u6027\\u80fd\\u5347\\u7ea7, PHP7\\u5728\\u591a\\u653e\\u7684\\u6d4b\\u8bd5\\u4e2d\\u90fd\\u8868\\u73b0\\u51fa\\u5f88\\u660e\\u663e\\u7684\\u6027\\u80fd\\u63d0\\u5347,\\u3002<\\/p><p><br\\/><\\/p><p>\\u7136\\u800c, \\u4e3a\\u4e86\\u8ba9\\u5b83\\u80fd\\u53d1\\u6325\\u51fa\\u6700\\u5927\\u7684\\u6027\\u80fd, \\u6211\\u8fd8\\u662f\\u6709\\u51e0\\u4ef6\\u4e8b\\u60f3\\u63d0\\u9192\\u4e0b.<\\/p><p><a href=\\\"http:\\/\\/laruence-blog.stor.sinaapp.com\\/uploads\\/2015\\/12\\/MYVQSTIBYX6KHZQ9XH72U.jpg\\\"><img src=\\\"\\/public\\/upload\\/image\\/20190417\\/1555491897534313.jpg\\\" alt=\\\"\\\" title=\\\"PHP7 VS PHP5.6\\\" class=\\\"size-large wp-image-3087\\\" width=\\\"500\\\"\\/><\\/a>PHP7 VS PHP5.6<\\/p><h3>1. Opcache<\\/h3><p>\\u8bb0\\u5f97\\u542f\\u7528Zend Opcache, \\u56e0\\u4e3aPHP7\\u5373\\u4f7f\\u4e0d\\u542f\\u7528Opcache\\u901f\\u5ea6\\u4e5f\\u6bd4PHP-5.6\\u542f\\u7528\\u4e86Opcache\\u5feb, \\u6240\\u4ee5\\u4e4b\\u524d\\u6d4b\\u8bd5\\u65f6\\u671f\\u5c31\\u53d1\\u751f\\u4e86\\u6709\\u4eba\\u4e00\\u76f4\\u6ca1\\u6709\\u542f\\u7528Opcache\\u7684\\u4e8b\\u60c5. \\u542f\\u7528Opcache\\u975e\\u5e38\\u7b80\\u5355, \\u5728php.ini\\u914d\\u7f6e\\u6587\\u4ef6\\u4e2d\\u52a0\\u5165:<\\/p><pre class=\\\"sh_bash\\\">zend_extension=opcache.soopcache.enable=1opcache.enable_cli=1&quot;<\\/pre><h3>2. \\u4f7f\\u7528\\u65b0\\u7684\\u7f16\\u8bd1\\u5668<\\/h3><p>\\u4f7f\\u7528\\u65b0\\u4e00\\u70b9\\u7684\\u7f16\\u8bd1\\u5668, \\u63a8\\u8350GCC 4.8\\u4ee5\\u4e0a, \\u56e0\\u4e3a\\u53ea\\u6709GCC 4.8\\u4ee5\\u4e0aPHP\\u624d\\u4f1a\\u5f00\\u542fGlobal Register for opline and execute_data\\u652f\\u6301, \\u8fd9\\u4e2a\\u4f1a\\u5e26\\u67655%\\u5de6\\u53f3\\u7684\\u6027\\u80fd\\u63d0\\u5347(Wordpres\\u7684QPS\\u89d2\\u5ea6\\u8861\\u91cf)<\\/p><p>\\u5176\\u5b9eGCC 4.8\\u4ee5\\u524d\\u7684\\u7248\\u672c\\u4e5f\\u652f\\u6301, \\u4f46\\u662f\\u6211\\u4eec\\u53d1\\u73b0\\u5b83\\u652f\\u6301\\u7684\\u6709Bug, \\u6240\\u4ee5\\u5fc5\\u987b\\u662f4.8\\u4ee5\\u4e0a\\u7684\\u7248\\u672c\\u624d\\u4f1a\\u5f00\\u542f\\u8fd9\\u4e2a\\u7279\\u6027.<\\/p><h3>3. &nbsp;HugePage<\\/h3><p>\\u6211\\u4e4b\\u524d\\u7684\\u6587\\u7ae0\\u4e5f\\u4ecb\\u7ecd\\u8fc7: <a href=\\\"http:\\/\\/www.laruence.com\\/2015\\/10\\/02\\/3069.html\\\">\\u8ba9\\u4f60\\u7684PHP7\\u66f4\\u5feb\\u4e4bHugepage<\\/a> , \\u9996\\u5148\\u5728\\u7cfb\\u7edf\\u4e2d\\u5f00\\u542fHugePages, \\u7136\\u540e\\u5f00\\u542fOpcache\\u7684huge_code_pages.<\\/p><p>\\u4ee5\\u6211\\u7684CentOS 6.5\\u4e3a\\u4f8b, \\u901a\\u8fc7:<\\/p><pre class=\\\"sh_bash\\\">$sudo&nbsp;sysctl&nbsp;vm.nr_hugepages=512<\\/pre><p>\\u5206\\u914d512\\u4e2a\\u9884\\u7559\\u7684\\u5927\\u9875\\u5185\\u5b58:<\\/p><pre class=\\\"sh_bash\\\">$&nbsp;cat&nbsp;\\/proc\\/meminfo&nbsp;&nbsp;|&nbsp;grep&nbsp;HugeAnonHugePages:&nbsp;&nbsp;&nbsp;&nbsp;106496&nbsp;\\r\\n\\r\\nkBHugePages_Total:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;512\\r\\n\\r\\nHugePages_Free:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;504\\r\\n\\r\\nHugePages_Rsvd:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;27\\r\\n\\r\\nHugePages_Surp:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;0\\r\\n\\r\\nHugepagesize:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2048&nbsp;kB<\\/pre><p>\\u7136\\u540e\\u5728php.ini\\u4e2d\\u52a0\\u5165:<\\/p><pre class=\\\"sh_bash\\\">&nbsp;opcache.huge_code_pages=1<\\/pre><p>\\u8fd9\\u6837\\u4e00\\u6765, PHP\\u4f1a\\u628a\\u81ea\\u8eab\\u7684text\\u6bb5, \\u4ee5\\u53ca\\u5185\\u5b58\\u5206\\u914d\\u4e2d\\u7684huge\\u90fd\\u91c7\\u7528\\u5927\\u5185\\u5b58\\u9875\\u6765\\u4fdd\\u5b58, \\u51cf\\u5c11TLB miss, \\u4ece\\u800c\\u63d0\\u9ad8\\u6027\\u80fd.<\\/p><h3>4. Opcache file cache<\\/h3><p>\\u5f00\\u542fOpcache File Cache(\\u5b9e\\u9a8c\\u6027), &nbsp;\\u901a\\u8fc7\\u5f00\\u542f\\u8fd9\\u4e2a, \\u6211\\u4eec\\u53ef\\u4ee5\\u8ba9Opcache\\u628aopcode\\u7f13\\u5b58\\u7f13\\u5b58\\u5230\\u5916\\u90e8\\u6587\\u4ef6\\u4e2d, \\u5bf9\\u4e8e\\u4e00\\u4e9b\\u811a\\u672c, \\u4f1a\\u6709\\u5f88\\u660e\\u663e\\u7684\\u6027\\u80fd\\u63d0\\u5347.<br\\/>\\r\\n &nbsp; \\u5728php.ini\\u4e2d\\u52a0\\u5165:<\\/p><pre class=\\\"sh_bash\\\">opcache.file_cache=\\/tmp<\\/pre><p>\\u8fd9\\u6837PHP\\u5c31\\u4f1a\\u5728\\/tmp\\u76ee\\u5f55\\u4e0bCache\\u4e00\\u4e9bOpcode\\u7684\\u4e8c\\u8fdb\\u5236\\u5bfc\\u51fa\\u6587\\u4ef6, \\u53ef\\u4ee5\\u8de8PHP\\u751f\\u547d\\u5468\\u671f\\u5b58\\u5728.<\\/p><h3>5. PGO<\\/h3><p>\\u6211\\u4e4b\\u524d\\u7684\\u6587\\u7ae0: <a href=\\\"http:\\/\\/www.laruence.com\\/2015\\/06\\/19\\/3063.html\\\">\\u8ba9\\u4f60\\u7684PHP7\\u66f4\\u5feb(GCC PGO)<\\/a> \\u4e5f\\u4ecb\\u7ecd\\u8fc7, \\u5982\\u679c\\u4f60\\u7684PHP\\u662f\\u4e13\\u95e8\\u4e3a\\u4e00\\u4e2a\\u9879\\u76ee\\u670d\\u52a1, \\u6bd4\\u5982\\u53ea\\u662f\\u4e3a\\u4f60\\u7684Wordpress, \\u6216\\u8005drupal, \\u6216\\u8005\\u5176\\u4ed6\\u4ec0\\u4e48, \\u90a3\\u4e48\\u4f60\\u5c31\\u53ef\\u4ee5\\